package com.example.intents;

import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.view.View;
import android.widget.ArrayAdapter;
import android.widget.ImageView;
import android.widget.Spinner;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;

import com.bumptech.glide.Glide;


public class ClRestaurant extends AppCompatActivity implements View.OnClickListener {

    TextView TTel, TUbi, TPag,TTrip;
    TextView TTel2, TUbi2, TPag2,TTrip2;
    TextView TTel3, TUbi3, TPag3,TTrip3;

    TextView[] tex;
    ImageView imgRestaurante,imgRestaurante2,imgRestaurante3,imgmatparc;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.restaurants);
        setupSpinnerBasic();
        setupSpinnerValor();
        imgRestaurante = findViewById(R.id.imgRes);
        imgRestaurante2 = findViewById(R.id.imgRes2);
        imgRestaurante3 = findViewById(R.id.imgRes3);
        imgmatparc = findViewById(R.id.matparc);

        TTel = findViewById(R.id.tel);
        TPag = findViewById(R.id.pag);
        TUbi = findViewById(R.id.ubi);
        TTrip = findViewById(R.id.trip);

        TTel2 = findViewById(R.id.tel2);
        TPag2 = findViewById(R.id.pag2);
        TUbi2 = findViewById(R.id.ubi2);
        TTrip2 = findViewById(R.id.trip2);

        TTel3 = findViewById(R.id.tel3);
        TPag3 = findViewById(R.id.pag3);
        TUbi3 = findViewById(R.id.ubi3);
        TTrip3 = findViewById(R.id.trip3);
        tex = new TextView[]{TTel, TPag, TUbi,TTrip,TTel2, TUbi2, TPag2,TTrip2,TTel3, TUbi3, TPag3,TTrip3};


        //Detectar clic botons:
        int i = 0;
        for (TextView vi : tex) {
            vi.setOnClickListener(this);
            vi.setTag(i);
            i++;
        }
        imgmatparc.setOnClickListener(this);
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .into(imgRestaurante);
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .into(imgRestaurante2);
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .into(imgRestaurante3);
    }

        @Override
        public void onClick (View v){
            if (v.getId() == R.id.matparc) {
                Intent pag = new Intent(this, MainActivity.class);
                startActivity(pag);
            }
            if (v.getId() == R.id.pag) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("https://www.udon.com/restaurante/udon-mataro-parc"));
                startActivity(intent);
            }
            if (v.getId() == R.id.pag2) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("https://www.facebook.com/muerdelapasta"));
                startActivity(intent);
            }
            if (v.getId() == R.id.pag3) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("https://fostershollywood.es/restaurantes/barcelona/mataro-parc/fh-mataro-parc?utm_source=directorios&utm_medium=organic&utm_campaign=estatica_directorios&utm_content=sitio_web"));
                startActivity(intent);
            }

            if (v.getId() == R.id.tel) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("tel:647328384"));
                startActivity(intent);
            }
            if (v.getId() == R.id.tel2) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("tel:647328384"));
                startActivity(intent);
            }
            if (v.getId() == R.id.tel3) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("tel:647328384"));
                startActivity(intent);
            }

        if (v.getId() == R.id.trip) {
            Intent intent = new Intent (Intent.ACTION_VIEW, Uri.parse("https://www.tripadvisor.es/Restaurant_Review-g642221-d4600677-Reviews-UDON_Mataro_Parc-Mataro_Catalonia.html"));
            startActivity(intent);
        }
            if (v.getId() == R.id.trip2) {
                Intent intent = new Intent (Intent.ACTION_VIEW, Uri.parse("https://www.tripadvisor.es/Restaurant_Review-g642221-d9699116-Reviews-Muerde_la_Pasta_Mataro_Parc-Mataro_Catalonia.html"));
                startActivity(intent);
            }

            if (v.getId() == R.id.trip3) {
                Intent intent = new Intent (Intent.ACTION_VIEW, Uri.parse("https://www.tripadvisor.es/Restaurant_Review-g642221-d8052597-Reviews-Foster_s_Hollywood-Mataro_Catalonia.html"));
                startActivity(intent);
            }
            if (v.getId() == R.id.ubi) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("geo:41.55463232709848, 2.4335997463713497"));
                startActivity(intent);
            }
            if (v.getId() == R.id.ubi2) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("geo:41.554789867736346, 2.433092993586719"));
                startActivity(intent);
            }
            if (v.getId() == R.id.ubi2) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("geo:41.554579051416596, 2.4334742880845104"));
                startActivity(intent);
            }

        }

    private void setupSpinnerBasic() {
        Spinner spinner = findViewById(R.id.spinner_basic);
        ArrayAdapter<CharSequence> adapter = ArrayAdapter.createFromResource(this,
                R.array.restauranteArry, android.R.layout.simple_spinner_item);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinner.setAdapter(adapter);


    }
    private void setupSpinnerValor() {
        Spinner spinner = findViewById(R.id.spinner_valor);
        ArrayAdapter<CharSequence> adapter = ArrayAdapter.createFromResource(this,
                R.array.valoracio_hotels, android.R.layout.simple_spinner_item);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinner.setAdapter(adapter);
         spinner = findViewById(R.id.spinner_valor2);
        adapter = ArrayAdapter.createFromResource(this,
                R.array.valoracio_hotels, android.R.layout.simple_spinner_item);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinner.setAdapter(adapter);
        spinner = findViewById(R.id.spinner_valor3);
        adapter = ArrayAdapter.createFromResource(this,
                R.array.valoracio_hotels, android.R.layout.simple_spinner_item);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinner.setAdapter(adapter);
    }
}

