package com.example.intents;

import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.view.View;
import android.widget.ArrayAdapter;
import android.widget.ImageView;
import android.widget.Spinner;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;

import com.bumptech.glide.Glide;


public class ClRestaurant extends AppCompatActivity implements View.OnClickListener {

    TextView TTel, TUbi, TPag,TTrip;
    TextView[] tex;
    ImageView imgRestaurante;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.restaurants);
        setupSpinnerBasic();
        imgRestaurante = findViewById(R.id.imgRes);

        TTel = findViewById(R.id.tel);
        TPag = findViewById(R.id.pag);
        TUbi = findViewById(R.id.ubi);
        TTrip = findViewById(R.id.trip);
        tex = new TextView[]{TTel, TPag, TUbi,TTrip};
        //Detectar clic botons:
        int i = 0;
        for (TextView vi : tex) {
            vi.setOnClickListener(this);
            vi.setTag(i);
            i++;
        }
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .into(imgRestaurante);
    }

        @Override
        public void onClick (View v){
            if (v.getId() == R.id.pag) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("https://www.udon.com/restaurante/udon-mataro-parc"));
                startActivity(intent);
            }

            if (v.getId() == R.id.tel) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("tel:647328384"));
                startActivity(intent);
            }

        if (v.getId() == R.id.trip) {
            Intent intent = new Intent (Intent.ACTION_VIEW, Uri.parse("https://www.tripadvisor.es/Restaurant_Review-g642221-d4600677-Reviews-UDON_Mataro_Parc-Mataro_Catalonia.html"));
            startActivity(intent);
        }

            if (v.getId() == R.id.ubi) {
                Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse("geo:41.55463232709848, 2.4335997463713497"));
                startActivity(intent);
            }
        }

    private void setupSpinnerBasic() {
        Spinner spinner = findViewById(R.id.spinner_basic);
        ArrayAdapter<CharSequence> adapter = ArrayAdapter.createFromResource(this,
                R.array.social_networks_array, android.R.layout.simple_spinner_item);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinner.setAdapter(adapter);


    }
}
